TranscendenceExtension.Globals:
(block (module)

(setq module (list
  '((weight     9)
    (title      (block nil
      (if (objGetData gPlayerShip 'god/keeptimestop)
        "TimeStop: Start"
        "TimeStop: Persist"
      )
    ))
    (subtitle   "Simply overpowering")
    (action     (block nil
      (if (objGetData gPlayerShip 'god/keeptimestop)
        (block nil
          (objSetData gPlayerShip 'god/keeptimestop nil)
          (dsf/description "Starting time again on exit")
        )
        (block nil
          (objSetData gPlayerShip 'god/keeptimestop true)
          (dsf/description "Keeping time stopped on exit")
        )
      )
      (dsf/show-screen)
    )))
))

;; handle modules
(setq god/modules (if (isError god/modules)
  module
  (append god/modules module)
))

)
